import React, { useState } from 'react';

export const UploadForm = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [file, setFile] = useState(null);

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí puedes realizar la lógica para subir el archivo
    // utilizando el valor de 'title', 'description' y 'file'
    // a través de una API o cualquier otro método.
    // Puedes imprimir los valores en la consola para verificarlos:
    console.log('Título:', title);
    console.log('Descripción:', description);
    console.log('Archivo:', file);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="mb-3">
        <label htmlFor="title" className="form-label">
          Título
        </label>
        <input
          type="text"
          className="form-control"
          id="title"
          value={title}
          onChange={handleTitleChange}
        />
      </div>
      <div className="mb-3">
        <label htmlFor="description" className="form-label">
          Descripción
        </label>
        <textarea
          className="form-control"
          id="description"
          value={description}
          onChange={handleDescriptionChange}
        ></textarea>
      </div>
      <div className="mb-3">
        <label htmlFor="file" className="form-label">
          Archivo
        </label>
        <input
          type="file"
          className="form-control"
          id="file"
          onChange={handleFileChange}
        />
      </div>
      <button type="submit" className="btn btn-primary">
        Subir Archivo
      </button>
    </form>
  );
};
